import React, { useState, useEffect, useContext } from "react";
import { Col, Container, Row, Modal, Button } from "react-bootstrap";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { UserContext } from "../../context/auth";
function MyModal(props) {
  return (
    <Modal
      {...props}
      size="md"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        {/* <Modal.Title id="contained-modal-title-vcenter">
          Modal heading
        </Modal.Title> */}
      </Modal.Header>
      <Modal.Body>
        <h4>Are you sure you want to cancel this form? </h4>
        <p>All of your input will be lost.</p>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={props.onHide} variant="outline-primary">
          Close
        </Button>
        <a href="/scholarships">
          <Button onClick={props.onHide}>Ok</Button>
        </a>
      </Modal.Footer>
    </Modal>
  );
}

const ApplicationForm = () => {
  const userEmail = localStorage.getItem("email");
  const {
    loggedIn,
    setLoggedIn,
    user,
    setUser,
    adminLoggedIn,
    setAdminLoggedIn,
  } = useContext(UserContext);

  const navigate = useNavigate();
  const params = useParams();
  const [scholarshipName, setScholarshipName] = useState("");
  const [amount, setAmount] = useState("");
  const [category, setCategory] = useState("");
  const [name, setName] = useState("");
  const [mobile, setMobile] = useState("");
  const [email, setEmail] = useState("");
  const [dob, setDob] = useState("");
  const [age, setAge] = useState("");
  const [gender, setGender] = useState("");
  const [address, setAddress] = useState("");
  const [country, setCountry] = useState("");
  const [adharCard, setAdharCard] = useState("");
  const [bankName, setBankName] = useState("");
  const [accountNumber, setAccountNumber] = useState("");
  const [branch, setBranch] = useState("");
  const [income, setIncome] = useState("");
  const [incomeProof, setIncomeProof] = useState("");
  const [collegeName, setCollegeName] = useState("");
  const [uid, setUid] = useState("");
  const [collegeEmail, setCollegeEmail] = useState("");
  const [degree, setDegree] = useState("");
  const [cgpa, setCgpa] = useState("");
  const [achievements, setAchievements] = useState("");
  const [extracurricular, setExtracurricular] = useState("");
  const [lor, setLor] = useState("");
  const [certificates, setCertificates] = useState("");
  const getSingleScholarship = async () => {
    try {
      // const id = params.id.toString();
      const { data } = await axios.get(
        `http://localhost:8080/scholarship/${params.id}`
      );
      // setUserId(data.scholarship._id);
      setScholarshipName(data.scholarship.scholarshipName);
      setAmount(data.scholarship.amount);
      setCategory(data.scholarship.category);
      setEmail(userEmail);
    } catch (error) {
      console.log(error);
      alert("Something went wrong");
    }
  };

  useEffect(() => {
    getSingleScholarship();
    //eslint-disable-next-line
  }, []);

  const [modalShow, setModalShow] = React.useState(false);
  const [step, setStep] = useState(1);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(`http://localhost:8080/application-form`, {
        scholarshipName,
        category,
        amount,
        name,
        mobile,
        email,
        dob,
        age,
        gender,
        address,
        country,
        adharCard,
        bankName,
        accountNumber,
        branch,
        income,
        incomeProof,
        collegeName,
        uid,
        collegeEmail,
        degree,
        cgpa,
        achievements,
        extracurricular,
        lor,
        certificates,
      });
      if (res && res.data.success) {
        alert(res.data.message);
        navigate("/user-dashboard");
        // refresh page after submit is done
        window.location.reload();
      } else {
        alert(res.data.message);
      }
    } catch (error) {
      console.log(error);
      alert("Something went wrong");
    }
  };

  const handleNext = (e) => {
    e.preventDefault();
    setStep((prevStep) => prevStep + 1);
  };

  const handlePrev = (e) => {
    e.preventDefault();
    setStep((prevStep) => prevStep - 1);
  };

  const renderStep = () => {
    switch (step) {
      case 1: //Personal-Details
        return (
          <>
            <Container>
              <div className="pd-20 card-box mb-30">
                <div className="clearfix">
                  <h3 className="text-blue " style={{ color: " #2b50c7" }}>
                    Personal Details
                  </h3>
                </div>
                {/* <form className="bg-transparent"> */}
                <Container>
                  {/* Name */}
                  <div className="form-group  ">
                    <Row>
                      <Col xs={12} sm={12} md={2}>
                        <label className="col-form-label">
                          Name <span className="compulsory">*</span>
                        </label>
                      </Col>
                      <Col xs={12} sm={12} md={10}>
                        <div className="">
                          <input
                            name="name"
                            className="form-control"
                            type="text"
                            placeholder="Name"
                            required
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                          />
                        </div>
                      </Col>
                    </Row>
                  </div>
                  {/* Mobile NUmber and Email*/}
                  <div className="form-group  ">
                    <Row>
                      <Col xs={12} sm={12} md={2}>
                        <label className="col-form-label">
                          Mobile Number <span className="compulsory">*</span>
                        </label>
                      </Col>
                      <Col xs={12} sm={12} md={3}>
                        <div className="">
                          <input
                            name="mobile"
                            required
                            className="form-control"
                            type="text"
                            placeholder="Enter Mobile Number"
                            value={mobile}
                            onChange={(e) => setMobile(e.target.value)}
                          />
                        </div>
                      </Col>
                      <Col xs={12} sm={12} md={1}>
                        <label className="col-form-label">
                          Email <span className="compulsory">*</span>
                        </label>
                      </Col>
                      <Col xs={12} sm={12} md={6}>
                        <div className="">
                          {/* <input
                            name="email"
                            required
                            className="form-control"
                            type="email"
                            placeholder="Enter email id"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                          /> */}
                          <input
                          plaintext readOnly name="email" className="form-control" value={email}/>
                        </div>
                      </Col>
                    </Row>
                  </div>
                  {/* DOB, Age and Gender */}
                  <div className="form-group  ">
                    <Row>
                      <Col xs={12} sm={12} md={2}>
                        <label className="    col-form-label">
                          Date of Birth <span className="compulsory">*</span>
                        </label>
                      </Col>
                      <Col xs={12} sm={12} md={3}>
                        <div className="   ">
                          <input
                            name="dob"
                            className="form-control datepicker"
                            placeholder="Select Date"
                            type="date"
                            required
                            value={dob}
                            onChange={(e) => setDob(e.target.value)}
                          />
                        </div>
                      </Col>
                      <Col xs={12} sm={12} md={1}>
                        <label className=" col-form-label">
                          Age <span className="compulsory">*</span>
                        </label>
                      </Col>
                      <Col xs={12} sm={12} md={3}>
                        <div className="   ">
                          <input
                            name="age"
                            className="form-control"
                            type="number"
                            required
                            placeholder="Enter Age"
                            value={age}
                            onChange={(e) => setAge(e.target.value)}
                          />
                        </div>
                      </Col>
                      <Col xs={12} sm={12} md={1}>
                        <label className="    col-form-label">
                          Gender <span className="compulsory">*</span>
                        </label>
                      </Col>
                      <Col xs={12} sm={12} md={2}>
                        <div className="   ">
                          <Col sm={12} md={12}>
                            <select
                              required
                              className="custom-select"
                              name="gender"
                              value={gender}
                              onChange={(e) => setGender(e.target.value)}
                            >
                              <option defaultValue="">Choose Gender </option>
                              <option value={"male"}>Male</option>
                              <option value={"female"}>Female</option>
                              <option value={"other"}>Other</option>
                            </select>
                          </Col>
                        </div>
                      </Col>
                    </Row>
                  </div>
                  {/* Address */}
                  <div className="form-group  ">
                    <Row>
                      <Col xs={12} sm={12} md={2}>
                        <label className="col-form-label">
                          Address <span className="compulsory">*</span>
                        </label>
                      </Col>
                      <Col xs={12} sm={12} md={6}>
                        <div className="">
                          <input
                            name="address"
                            required
                            className="form-control"
                            type="text"
                            placeholder="Enter Address"
                            value={address}
                            onChange={(e) => setAddress(e.target.value)}
                          />
                        </div>
                      </Col>
                      <Col xs={12} sm={12} md={1}>
                        <label className="col-form-label">
                          Country<span className="compulsory">*</span>
                        </label>
                      </Col>
                      <Col xs={12} sm={12} md={3}>
                        <div className="">
                          <input
                            name="country"
                            className="form-control"
                            type="text"
                            required
                            placeholder="Country"
                            value={country}
                            onChange={(e) => setCountry(e.target.value)}
                          />
                        </div>
                      </Col>
                    </Row>
