import React, { useState ,useContext} from "react";
import { Modal, Button, Container, Row, Col, Form } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { UserContext } from "../../context/auth";

function MyVerticallyCenteredModal(props) {
  return (
    <Modal
      {...props}
      size="md"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        {/* <Modal.Title id="contained-modal-title-vcenter">
            Modal heading
          </Modal.Title> */}
      </Modal.Header>
      <Modal.Body>
        <h4>Are you sure you want to cancel this form? </h4>
        <p>All of your input will be lost.</p>
      </Modal.Body>
      <Modal.Footer>
        <Button onClick={props.onHide} variant="outline-primary">
          Close
        </Button>
        <a href="/merit-scholarships">
          <Button onClick={props.onHide}>Ok</Button>
        </a>
      </Modal.Footer>
    </Modal>
  );
}

const AddScholarship = () => {
  const navigate = useNavigate();
  const { loggedIn,
    setLoggedIn,
    user,
    setUser,
    adminLoggedIn,
    setAdminLoggedIn, } = useContext(UserContext);
  const [modalShow, setModalShow] = React.useState(false);
  const [scholarshipName, setScholarshipName] = useState("");
  const [deadline, setDeadline] = useState("");
  const [amount, setAmount] = useState("");
  const [category, setCategory] = useState("");
  const [eligibility, setEligibility] = useState("");
  const [documents, setDocuments] = useState("");
  const [description, setDescription] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if(adminLoggedIn){
      const res = await axios.post(`http://localhost:8080/createscholarships`, {
        scholarshipName,
        deadline,
        category,
        amount,
        eligibility,
        documents,
        description,
      });
      if (res && res.data.success) {
        alert(res.data.message);
        navigate("/adminDashboard");
        // Reset the inputs to blank
        e.target.reset();
        setScholarshipName("");
        setDeadline("");
        setCategory("");
        setAmount("");
        setEligibility("");
        setDocuments("");
        setDescription("");
        // refresh page after submit is done
        window.location.reload();
      } else {
        alert(res.data.message);
      }}
      else{
        console.log("Unautherized Access")
      }
    } catch (error) {
      console.log(error);
      alert("Something went wrong");
    }
  };

  return (
    <>
      {/* Scholarship Creation form */}
      <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.5.1/jquery.min.js"></script>
      {/* <Navbar /> */}
      <Container>
        <div className="pd-20 card-box mb-30">
          <div className="clearfix">
            <h3
              className="text-blue "
              style={{ color: " #2b50c7", textAlign: "center" }}
            >
              Create Scholarship Form
            </h3>
          </div>
          <Form
            className="bg-transparent"
            method="POST"
            onSubmit={handleSubmit}
          >
            <Container>
              {/* Name */}
