                  </Col>
                  <Col xs={12} sm={12} md={10}>
                    <div className="">
                      <input
                        name="name"
                        className="form-control"
                        type="text"
                        placeholder="Enter Scholarship Name"
                        required
                        value={scholarshipName}
                        onChange={(e) => setScholarshipName(e.target.value)}
                      />
                    </div>
                  </Col>
                </Row>
              </div>

              {/* Deadline */}
              <div className="form-group  ">
                <Row>
                  <Col xs={12} sm={12} md={2}>
                    <label className="    col-form-label">
                      Deadline <span className="compulsory">*</span>
                    </label>
                  </Col>
                  <Col xs={12} sm={12} md={2}>
                    <div className="   ">
                      <input
                        name="deadline"
                        className="form-control datepicker"
                        type="date"
                        required
                        value={deadline}
                        onChange={(e) => setDeadline(e.target.value)}
                      />
                    </div>
                  </Col>
                  <Col xs={12} sm={12} md={1}>
                    <label className="    col-form-label">
                      Amount <span className="compulsory">*</span>
                    </label>
                  </Col>
                  <Col xs={12} sm={12} md={2}>
                    <div className="   ">
                      <input
                        name="amount"
                        className="form-control"
                        type="number"
                        required
                        value={amount}
                        onChange={(e) => setAmount(e.target.value)}
                      />
                    </div>
                  </Col>

                  <Col xs={12} sm={12} md={1}>
                    <label className="    col-form-label">
                      Category <span className="compulsory">*</span>
                    </label>
                  </Col>
                  <Col xs={12} sm={12} md={4}>
                    <div className="   ">
                      <Col sm={12} md={12}>
                        {/* <div className="   ">
                      <input
                        name="category"
                        className="form-control "
                        type="text"
                        required
                        value={category}
                        onChange={(e) => setCategory(e.target.value)}
                      />
                    </div> */}
                        <Form.Select
                          name="category"
                          value={category}
                          className="custom-select"
                          onChange={(e) => {
                            setCategory(e.target.value);
                          }}
                          required
                        >
                          <option value={""}>
                            Choose Scholarship Category{""}
                          </option>
                          <option value={"merit-based"}>Merit</option>
                          <option value={"minority-based"}>Minority</option>
                          <option value={"need-based"}>Need based</option>
                          <option value={"international"}>International</option>
                          <option value={"research-based"}>
                            Research Based
                          </option>
                        </Form.Select>
                      </Col>
                    </div>
                  </Col>
                </Row>
              </div>
              {/* Eligibility Criteria */}
              <div className="form-group  ">
                <Row>
                  <Col xs={12} sm={12} md={2}>
                    <label className="col-form-label">
                      Eligibility Criteria <span className="compulsory">*</span>
                    </label>
                  </Col>
                  <Col xs={12} sm={12} md={10}>
                    <div className="">
                      <input
                        name="eligibility"
                        className="form-control"
                        type="text"
                        placeholder="Enter Eligibility Criteria"
                        value={eligibility}
                        onChange={(e) => setEligibility(e.target.value)}
                        required
                      />
                    </div>
                  </Col>
                </Row>
              </div>
              {/* Documents Required */}
              <div className="form-group  ">
                <Row>
                  <Col xs={12} sm={12} md={2}>
                    <label className="col-form-label">
                      Documents Required <span className="compulsory">*</span>
                    </label>
                  </Col>
                  <Col xs={12} sm={12} md={10}>
                    <div className="">
                      <input
                        name="documents"
                        className="form-control"
                        type="text"
